// src/components/CustomerList.js
import React, { useState, useEffect } from 'react';
import ReactPaginate from 'react-paginate';
import { Link } from 'react-router-dom';

const CustomerList = () => {
  const [customers, setCustomers] = useState([]);
  const [totalPages, setTotalPages] = useState(0);
  const [currentPage, setCurrentPage] = useState(0);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchCustomers = async () => {
      setLoading(true);
      try {
        const response = await fetch(`http://localhost:5000/customers?limit=5&offset=${currentPage * 5}`);
        if (response.ok) {
          const data = await response.json();
          setCustomers(data.customers);
          setTotalPages(data.totalPages);
        } else {
          throw new Error('Failed to fetch customers');
        }
      } catch (error) {
        setError(error.message);
      }
      setLoading(false);
    };

    fetchCustomers();
  }, [currentPage]);

  const handlePageChange = ({ selected }) => {
    setCurrentPage(selected);
  };

  const handleDelete = async (id) => {
    if (window.confirm('Are you sure you want to delete this customer?')) {
      try {
        const response = await fetch(`http://localhost:5000/customers/${id}`, {
          method: 'DELETE'
        });
        if (response.ok) {
          setCustomers(customers.filter(customer => customer.id !== id));
        } else {
          throw new Error('Failed to delete customer');
        }
      } catch (error) {
        setError(error.message);
      }
    }
  };

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error: {error}</p>;

  return (
    <div>
      <h1>Customer List</h1>
      <Link to="/customers/create">Add New Customer</Link>
      <ul>
        {customers.map(customer => (
          <li key={customer.id}>
            <Link to={`/customers/${customer.id}`}>{customer.firstName} {customer.lastName}</Link>
            <button onClick={() => handleDelete(customer.id)}>Delete</button>
            <Link to={`/customers/update/${customer.id}`}>Edit</Link>
          </li>
        ))}
      </ul>
      <ReactPaginate
        pageCount={totalPages}
        onPageChange={handlePageChange}
        containerClassName={'pagination'}
        pageClassName={'page-item'}
        pageLinkClassName={'page-link'}
        previousClassName={'page-item'}
        previousLinkClassName={'page-link'}
        nextClassName={'page-item'}
        nextLinkClassName={'page-link'}
        breakClassName={'page-item'}
        breakLinkClassName={'page-link'}
        activeClassName={'active'}
      />
    </div>
  );
};

export default CustomerList;
