import React, { Component } from 'react';
import ReactPaginate from 'react-paginate';

class CustomerList extends Component {
  state = {
    customers: [],
    totalPages: 0,
    currentPage: 0
  };

  componentDidMount() {
    this.fetchCustomers(0);  // Fetch the first page
  }

  fetchCustomers = async (page) => {
    const limit = 5;
    const offset = page * limit;

    try {
      const response = await fetch(`http://localhost:5000/customers?limit=${limit}&offset=${offset}`);
      const data = await response.json();

      this.setState({
        customers: data.customers,
        totalPages: data.totalPages,
        currentPage: page
      });
    } catch (error) {
      console.error('Error fetching customers:', error);
    }
  };

  handlePageClick = (event) => {
    const selectedPage = event.selected;
    this.fetchCustomers(selectedPage);
  };

  render() {
    const { customers, totalPages } = this.state;

    return (
      <div>
        <h1>Customer List</h1>
        <ul>
          {customers.map(customer => (
            <li key={customer.id}>
              {customer.firstName} {customer.lastName} - {customer.email}
            </li>
          ))}
        </ul>

        {/* Pagination */}
        <ReactPaginate
          previousLabel={"← Previous"}
          nextLabel={"Next →"}
          pageCount={totalPages}
          onPageChange={this.handlePageClick}
          containerClassName={"pagination"}
          activeClassName={"active"}
        />
      </div>
    );
  }
}

export default CustomerList;
