import React, { Component } from 'react';

class CustomerForm extends Component {
  state = {
    firstName: '',
    lastName: '',
    phone: '',
    email: '',
    address: '',
    errors: {}
  };

  validateForm = () => {
    const { firstName, lastName, phone, email } = this.state;
    const errors = {};

    if (!/^[a-zA-Z]+$/.test(firstName)) errors.firstName = 'First name should only contain letters.';
    if (!/^[a-zA-Z]+$/.test(lastName)) errors.lastName = 'Last name should only contain letters.';
    if (!/^\d{10}$/.test(phone)) errors.phone = 'Phone number should be 10 digits long.';
    if (!/^\S+@\S+\.\S+$/.test(email)) errors.email = 'Email format is invalid.';

    this.setState({ errors });
    return Object.keys(errors).length === 0;
  };

  handleSubmit = async (e) => {
    e.preventDefault();
    if (!this.validateForm()) return;

    const { firstName, lastName, phone, email, address } = this.state;

    try {
      const response = await fetch('http://localhost:5000/customers', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ firstName, lastName, phone, email, address })
      });

      if (response.ok) {
        alert('Customer added successfully');
        this.setState({ firstName: '', lastName: '', phone: '', email: '', address: '' });
      } else {
        alert('Failed to add customer');
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  handleChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };

  render() {
    const { firstName, lastName, phone, email, address, errors } = this.state;
    return (
      <form onSubmit={this.handleSubmit}>
        <div>
          <label>First Name</label>
          <input type="text" name="firstName" value={firstName} onChange={this.handleChange} />
          {errors.firstName && <p>{errors.firstName}</p>}
        </div>
        <div>
          <label>Last Name</label>
          <input type="text" name="lastName" value={lastName} onChange={this.handleChange} />
          {errors.lastName && <p>{errors.lastName}</p>}
        </div>
        <div>
          <label>Phone</label>
          <input type="text" name="phone" value={phone} onChange={this.handleChange} />
          {errors.phone && <p>{errors.phone}</p>}
        </div>
        <div>
          <label>Email</label>
          <input type="text" name="email" value={email} onChange={this.handleChange} />
          {errors.email && <p>{errors.email}</p>}
        </div>
        <div>
          <label>Address</label>
          <input type="text" name="address" value={address} onChange={this.handleChange} />
        </div>
        <button type="submit">Add Customer</button>
      </form>
    );
  }
}

export default CustomerForm;
